declare const _default: "<!doctype html>\n<html>\n\n<head>\n    <meta name=\"viewport\" content=\"width=device-width\" />\n    <meta http-equiv=\"Content-Type\" content=\"text/html; charset=UTF-8\" />\n    <title>{{header}}</title>\n    <style>\n        /* -------------------------------------\n        GLOBAL RESETS\n        ------------------------------------- */\n\n        img {\n            border: none;\n            -ms-interpolation-mode: bicubic;\n            max-width: 100%;\n        }\n\n        body {\n            background-color: #f5f5f5;\n            font-family: sans-serif;\n            -webkit-font-smoothing: antialiased;\n            font-size: 14px;\n            line-height: 1.4;\n            -ms-text-size-adjust: 100%;\n            -webkit-text-size-adjust: 100%;\n        }\n\n\n        table {\n            border-collapse: separate;\n            mso-table-lspace: 0pt;\n            mso-table-rspace: 0pt;\n            width: 100%;\n        }\n\n        table td {\n            font-family: sans-serif;\n            font-size: 14px;\n            vertical-align: top;\n        }\n\n        /* -------------------------------------\n        BODY & CONTAINER\n        ------------------------------------- */\n\n        .body {\n            background-color: #f5f5f5;\n            width: 100%;\n        }\n\n        /* Set a max-width, and make it display as block so it will automatically stretch to that width, but will also shrink down on a phone or something */\n\n\n\n        /* -------------------------------------\n        HEADER, FOOTER, MAIN, DISCLAIMER\n        ------------------------------------- */\n\n\n        .wrapper {\n            box-sizing: border-box;\n            padding: 20px;\n        }\n\n        .warningmsg {\n            clear: both;\n            padding-top: 10px;\n            text-align: center;\n            width: 100%;\n        }\n\n        .warningmsg p,\n        .warningmsg span {\n            color: #999999;\n            font-size: 12px;\n        }\n\n        .warningmsg a {\n            color: #757373;\n            font-size: 12px;\n        }\n\n\n        /* -------------------------------------\n        TYPOGRAPHY\n        ------------------------------------- */\n\n        h1,\n        h2,\n        h3,\n        h4 {\n            color: #000000;\n            font-family: sans-serif;\n            font-weight: 400;\n            line-height: 1.4;\n            margin: 0;\n            Margin-bottom: 30px;\n        }\n\n        h1 {\n            font-size: 35px;\n            font-weight: 300;\n            text-align: center;\n            text-transform: capitalize;\n        }\n\n        p,\n        ul,\n        ol {\n            font-family: sans-serif;\n            font-size: 14px;\n            font-weight: normal;\n            margin: 0;\n            Margin-bottom: 15px;\n        }\n\n        p li,\n        ul li,\n        ol li {\n            list-style-position: inside;\n            margin-left: 5px;\n        }\n\n        a {\n            color: #3498db;\n            text-decoration: underline;\n        }\n\n        /* -------------------------------------\n        BUTTONS\n        ------------------------------------- */\n\n        .btn {\n            box-sizing: border-box;\n            width: 100%;\n        }\n\n        .btn>tbody>tr>td {\n            padding-bottom: 15px;\n        }\n\n        .btn table {\n            width: auto;\n        }\n\n        .btn table td {\n            background-color: #ffffff;\n            border-radius: 5px;\n            text-align: center;\n        }\n\n        .btn a {\n            background-color: #ffffff;\n            border: solid 1px #3498db;\n            border-radius: 5px;\n            box-sizing: border-box;\n            color: #3498db;\n            cursor: pointer;\n            display: inline-block;\n            font-size: 14px;\n            font-weight: bold;\n            margin: 0;\n            padding: 12px 25px;\n            text-decoration: none;\n            text-transform: capitalize;\n        }\n\n        .btn-primary table td {\n            background-color: #3498db;\n        }\n\n        .btn-primary a {\n            background-color: #3498db;\n            border-color: #3498db;\n            color: #ffffff;\n        }\n\n        /* -------------------------------------\n        OTHER STYLES THAT MIGHT BE USEFUL\n        ------------------------------------- */\n\n        .last {\n            margin-bottom: 0;\n        }\n\n        .first {\n            margin-top: 0;\n        }\n\n        .align-center {\n            text-align: center;\n        }\n\n        .align-right {\n            text-align: right;\n        }\n\n        .align-left {\n            text-align: left;\n        }\n\n        .clear {\n            clear: both;\n        }\n\n        .mt0 {\n            margin-top: 0;\n        }\n\n        .mb0 {\n            margin-bottom: 0;\n        }\n\n        .powered-by a {\n            text-decoration: none;\n        }\n\n        phone {\n            content: \"\f437\";\n        }\n\n        hr {\n            border: 0;\n            border-bottom: 1px solid #f6f6f6;\n            Margin: 20px 0;\n        }\n\n        /* -------------------------------------\n        RESPONSIVE AND MOBILE FRIENDLY STYLES\n        ------------------------------------- */\n    </style>\n</head>\n\n<body style=\"overflow-x:hidden\">\n\n    <table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" class=\"body\">\n        <tr>\n            <td>&nbsp;</td>\n            <td\n                style=\"display: block;margin: 0 auto !important;max-width: 580px;padding: 0;width: auto !important;width: 100%;\">\n                <div>\n                    <!-- START CENTERED WHITE CONTAINER -->\n                    <span\n                        style=\"color: transparent;display: none;height: 0;max-height: 0;max-width: 0;opacity: 0;overflow: hidden;mso-hide: all;visibility: hidden;width: 0;\">{{header}}</span>\n                    <table style=\"background: #f5f5f5;border-radius: 3px;width: 100%;\">\n                        <!-- START MAIN CONTENT AREA -->\n                        <tr>\n                            <td style=\"box-sizing: border-box;padding: 10px;\">\n                                <table border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\n                                    <tr>\n                                        <td>\n                                            <div\n                                                style=\"font-family: Arial, sans-serif;margin: 0;padding: 0;display: flex;justify-content: center;align-items: center;min-width: 330px;\">\n                                                <div\n                                                    style=\"background-color: #fff;border-radius: 8px;box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);padding: 20px;max-width: 380px;width: 100%;\">\n                                                    <div style=\"text-align: center;padding-bottom: 10px;\">\n                                                        <table style=\"width: 100%;\">\n                                                            <tr>\n                                                                <th style=\"text-align: right;\">\n                                                                    Transaction Receipt\n                                                                </th>\n                                                            </tr>\n                                                        </table>\n                                                        <p\n                                                            style=\"margin: 0;color: #4CAF50;font-size: 18px;font-weight: bold;\">\n                                                            {{currency}}{{amount}}</p>\n                                                        <p style=\"font-weight: 600;font-size: small;color: #504d4d\">\n                                                            Successful</p>\n                                                        <p style=\"font-weight: 600;font-size: small;color: #504d4d\">\n                                                            {{date}}</p>\n                                                    </div>\n                                                    <span\n                                                        style=\"border-bottom: 2px dotted #6a6767; display: inline-block; height: 1px; width: 100%;\"></span>\n                                                    <div style=\"padding: 10px 0;\">\n\n                                                        <table style=\"width: 100%;\">\n                                                            <tr>\n                                                                <th\n                                                                    style=\"text-align: left;line-height: 2.5;font-weight: bold;color: #6a6767;font-size: small;\">\n                                                                    Transaction type:\n                                                                </th>\n                                                                <th\n                                                                    style=\"text-align: right;line-height: 2.5;font-weight: 500;color: #5b5b5b;font-size: small;\">\n                                                                    {{transactionType}}\n                                                                </th>\n                                                            </tr>\n                                                            <tr>\n                                                                <th\n                                                                    style=\"text-align: left;line-height: 2.5;font-weight: bold;color: #6a6767;font-size: small;\">\n                                                                    Recipient Details:\n                                                                </th>\n                                                                <th\n                                                                    style=\"text-align: right;line-height: 2.5;font-weight: 500;color: #5b5b5b;font-size: small;\">\n                                                                    VitalReg NPC\n                                                                </th>\n                                                            </tr>\n                                                            <tr>\n                                                                <th\n                                                                    style=\"text-align: left;line-height: 2.5;font-weight: bold;color: #6a6767;font-size: small;\">\n                                                                    Sender Details:\n                                                                </th>\n                                                                <th\n                                                                    style=\"text-align: right;line-height: 2.5;font-weight: 500;color: #5b5b5b;font-size: small;\">\n                                                                    {{payee}}\n                                                                </th>\n                                                            </tr>\n                                                            <tr>\n                                                                <th\n                                                                    style=\"text-align: left;line-height: 2.5;font-weight: bold;color: #6a6767;font-size: small;\">\n                                                                    Transaction ID:\n                                                                </th>\n                                                                <th\n                                                                    style=\"text-align: right;line-height: 2.5;font-weight: 500;color: #5b5b5b;font-size: small;\">\n                                                                    PY-{{transactionId}}\n                                                                </th>\n                                                            </tr>\n                                                            <tr>\n                                                                <th\n                                                                    style=\"text-align: left;line-height: 2.5;font-weight: bold;color: #6a6767;font-size: small;\">\n                                                                    Transaction Reference:\n                                                                </th>\n                                                                <th\n                                                                    style=\"text-align: right;line-height: 2.5;font-weight: 500;color: #5b5b5b;font-size: small;\">\n                                                                    {{reference}}\n                                                                </th>\n                                                            </tr>\n                                                        </table>\n                                                        <span\n                                                            style=\"border-bottom: 2px dotted #6a6767; display: inline-block; height: 1px; width: 100%;\"></span>\n                                                        <div style=\"padding-top: 10px;\">\n                                                            <small style=\"font-weight: small;\">Support: <a\n                                                                    href=\"mailto:support@idlms.gov.ng\">support@idlms.gov.ng</a>\n                                                            </small>\n                                                            <br />\n                                                            <small style=\"color: rgb(175, 58, 0);font-size: 12px;\"><b>REFUND\n                                                                    POLICY</b>: all payments to the goverment are not\n                                                                refundable</small>\n                                                        </div>\n                                                    </div>\n                                                </div>\n                                            </div>\n\n                                            <div\n                                                style=\"color: #999999;font-size: 12px;clear: both;padding-top: 10px;text-align: center;width: 100%;\">\n                                                <p style=\"color: #000000;\">The {{app_name}} Team</p>\n                                                <br />\n                                                <p>{{disclaimer}}</p>\n                                                <br />\n                                                <p style=\"color: #000000;\">Copyright \u00A9 {{date}} {{app_name}} <br> All Rights Reserved.</p>\n                                            </div>\n                                        </td>\n                                    </tr>\n                                </table>\n                            </td>\n                        </tr>\n                        <!-- END MAIN CONTENT AREA -->\n                    </table>\n                </div>\n            </td>\n            <td>&nbsp;</td>\n        </tr>\n    </table>\n</body>\n\n</html>";
export default _default;
